import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Mic, MicOff, Phone, PhoneOff, Video, VideoOff, Settings, MessageSquare,
  User, Bot, Volume2, VolumeX, Camera, CameraOff, MoreVertical, Minimize2, Maximize2
} from 'lucide-react';
import { useAuth } from '../hooks/useAuth';
import { toast } from 'react-hot-toast';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  metadata?: {
    language?: string;
    confidence?: number;
    voice_ready?: string[];
    intent?: string;
    suggestions?: string[];
  };
}

const Chat: React.FC = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('hi');
  const [isRecording, setIsRecording] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Video call states
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [isKrishiMitraSpeaking, setIsKrishiMitraSpeaking] = useState(false);
  const [currentSpeaker, setCurrentSpeaker] = useState<'farmer' | 'krishimitra' | null>(null);
  const [realtimeSession, setRealtimeSession] = useState<any>(null);
  const [isRealtimeActive, setIsRealtimeActive] = useState(false);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const krishiMitraVideoRef = useRef<HTMLVideoElement>(null);

  // Languages
  const languages = [
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'bn', name: 'বাংলা', flag: '🇮🇳' },
    { code: 'ta', name: 'தமிழ்', flag: '🇮🇳' },
    { code: 'te', name: 'తెలుగు', flag: '🇮🇳' },
    { code: 'mr', name: 'मराठी', flag: '🇮🇳' },
    { code: 'gu', name: 'ગુજરાતી', flag: '🇮🇳' },
    { code: 'pa', name: 'ਪੰਜਾਬੀ', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' },
    { code: 'ml', name: 'മലയാളം', flag: '🇮🇳' },
    { code: 'kn', name: 'ಕನ್ನಡ', flag: '🇮🇳' },
    { code: 'en', name: 'English', flag: '🇮🇳' }
  ];

  // Quick actions
  const quickActions = [
    { text: 'फसल की सलाह', action: 'crop' },
    { text: 'ऋण की जानकारी', action: 'loan' },
    { text: 'मंडी भाव', action: 'market' },
    { text: 'मौसम की जानकारी', action: 'weather' },
    { text: 'सरकारी योजनाएं', action: 'schemes' }
  ];

  // Add welcome message on mount
  useEffect(() => {
    const welcomeMessage: Message = {
      id: 'welcome',
      text: `नमस्ते ${user?.name || 'किसान'}! मैं KrishiMitra आपकी कैसे मदद कर सकता हूं? बोलकर अपना सवाल पूछें - फसल, ऋण, मंडी भाव, मौसम या सरकारी योजनाओं के बारे में।`,
      sender: 'ai',
      timestamp: new Date(),
      metadata: {
        voice_ready: [`नमस्ते ${user?.name || 'किसान'}! मैं KrishiMitra आपकी कैसे मदद कर सकता हूं?`],
        intent: 'welcome',
        suggestions: ['फसल की सलाह लें', 'ऋण की जानकारी लें', 'मंडी भाव जानें', 'मौसम की जानकारी लें']
      }
    };
    setMessages([welcomeMessage]);
  }, [user]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const addMessage = (message: Message) => {
    setMessages(prev => [...prev, message]);
  };

  const startRealtimeSession = async () => {
    try {
      toast.success('🔄 KrishiMitra Realtime session शुरू हो रहा है...');

      const response = await fetch('/api/v1/voice/realtime/session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          voice: 'alloy', 
          language: selectedLanguage, 
          user_id: user?.id,
          farmer_context: {
            location: "Punjab, India",
            crops: ["wheat", "rice", "cotton"],
            experience: "15 years",
            land_size: "10 acres",
            current_concerns: ["loan repayment", "crop selection", "market prices"]
          }
        }),
      });

      if (!response.ok) { 
        throw new Error('Failed to create realtime session'); 
      }

      const sessionData = await response.json();
      setRealtimeSession(sessionData);
      setIsRealtimeActive(true);
      toast.success('✅ KrishiMitra Realtime session सक्रिय है! अब आप बोल सकते हैं।');

    } catch (error) {
      console.error('Error starting realtime session:', error);
      toast.error('❌ Realtime session शुरू करने में समस्या। कृपया फिर से कोशिश करें।');
    }
  };

  const stopRealtimeSession = () => {
    setRealtimeSession(null);
    setIsRealtimeActive(false);
    toast.success('🛑 KrishiMitra Realtime session बंद हो गया है।');
  };

  const handleQuickAction = async (action: string) => {
    const actionTexts = {
      'crop': 'मुझे फसल की सलाह चाहिए',
      'loan': 'मुझे ऋण की जानकारी चाहिए',
      'market': 'मंडी के भाव बताएं',
      'weather': 'मौसम की जानकारी दें',
      'schemes': 'सरकारी योजनाओं के बारे में बताएं'
    };

    const text = actionTexts[action as keyof typeof actionTexts] || action;
    await sendMessage(text, selectedLanguage);
  };

  const handleComprehensiveAnalysis = async () => {
    const comprehensiveQuery = "मुझे पंजाब में किसान के रूप में कर्ज मिल सकता है, कौन सी फसल उगानी चाहिए, कितनी आमदनी होगी, कितना समय लगेगा चुकाने में और कौन सी कृषि तकनीकें इस्तेमाल कर सकता हूं, सुझाई गई फसल का बाजार भाव क्या है, कर्ज की नीति क्या है, किसान के रूप में मेरे लिए कोई ऑफर है, कितना जोखिम है";
    await sendMessage(comprehensiveQuery, selectedLanguage);
  };

  const sendMessage = async (text: string, language: string) => {
    if (!text.trim()) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text,
      sender: 'user',
      timestamp: new Date(),
      metadata: { language }
    };

    addMessage(userMessage);
    setIsLoading(true);

    try {
      // Send to backend for processing
      const response = await fetch('/api/v1/chat/send', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          message: text,
          language: language,
          user_id: user?.id
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to send message');
      }

      const data = await response.json();

      const aiMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: data.text || 'माफ़ करें, अभी कुछ तकनीकी समस्या है।',
        sender: 'ai',
        timestamp: new Date(),
        metadata: {
          language: data.language,
          confidence: data.confidence,
          voice_ready: data.voice_ready,
          intent: data.intent,
          suggestions: data.suggestions
        }
      };

      addMessage(aiMessage);

    } catch (error) {
      console.error('Error sending message:', error);

      // Fallback response
      const fallbackMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: 'माफ़ करें, अभी कुछ तकनीकी समस्या है। कृपया कुछ देर बाद फिर से कोशिश करें।',
        sender: 'ai',
        timestamp: new Date(),
        metadata: { language }
      };
      addMessage(fallbackMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;
    await sendMessage(inputText, selectedLanguage);
    setInputText('');
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Video call controls
  const toggleVideo = () => setIsVideoOn(!isVideoOn);
  const toggleAudio = () => setIsMuted(!isMuted);
  const toggleFullscreen = () => setIsFullscreen(!isFullscreen);

  return (
    <div className={`min-h-screen bg-gradient-to-br from-green-50 to-blue-50 ${isFullscreen ? 'fixed inset-0 z-50' : ''}`}>
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-blue-500 rounded-full flex items-center justify-center">
                <Bot className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-lg font-semibold text-gray-900">KrishiMitra Video Call</h1>
                <p className="text-sm text-gray-500">
                  {isRealtimeActive ? '🟢 Realtime Active' : '🔴 Realtime Inactive'}
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                {languages.map(lang => (
                  <option key={lang.code} value={lang.code}>
                    {lang.flag} {lang.name}
                  </option>
                ))}
              </select>

              <button
                onClick={toggleFullscreen}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
              >
                {isFullscreen ? <Minimize2 className="w-5 h-5" /> : <Maximize2 className="w-5 h-5" />}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Video Call Interface */}
      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 h-[calc(100vh-200px)]">

          {/* Farmer's Video Panel */}
          <div className="relative bg-gray-900 rounded-xl overflow-hidden shadow-2xl">
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center text-white">
                <User className="w-24 h-24 mx-auto mb-4 opacity-50" />
                <p className="text-lg font-semibold">{user?.name || 'किसान'}</p>
                <p className="text-sm opacity-75">आपका वीडियो</p>
              </div>
            </div>

            {/* Recording indicator */}
            {isRecording && (
              <div className="absolute top-4 left-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
                🔴 रिकॉर्डिंग
              </div>
            )}

            {/* Audio indicator */}
            {isMuted && (
              <div className="absolute top-4 right-4 bg-gray-800 text-white px-3 py-1 rounded-full text-sm">
                🔇 म्यूट
              </div>
            )}
          </div>

          {/* KrishiMitra's Video Panel */}
          <div className="relative bg-gray-900 rounded-xl overflow-hidden shadow-2xl">
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center text-white">
                <Bot className="w-24 h-24 mx-auto mb-4 opacity-50" />
                <p className="text-lg font-semibold">KrishiMitra AI</p>
                <p className="text-sm opacity-75">AI सहायक</p>
              </div>
            </div>

            {/* AI Status */}
            <div className="absolute top-4 right-4 flex space-x-2">
              <div className={`px-3 py-1 rounded-full text-sm ${
                isRealtimeActive 
                  ? 'bg-blue-500 text-white animate-pulse' 
                  : 'bg-green-500 text-white'
              }`}>
                {isRealtimeActive ? '🔄 Realtime' : 'AI सक्रिय'}
              </div>
            </div>

            {/* Speaking indicator */}
            {isKrishiMitraSpeaking && (
              <div className="absolute bottom-4 left-4 bg-green-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
                🗣️ बोल रहा है
              </div>
            )}
          </div>
        </div>

        {/* Control Panel */}
        <div className="mt-6 bg-white rounded-xl shadow-lg p-6">
          <div className="flex items-center justify-between">

            {/* Left Controls */}
            <div className="flex items-center space-x-4">
              <button onClick={toggleAudio} className={`p-4 rounded-full transition-all duration-200 ${isMuted ? 'bg-red-500 text-white hover:bg-red-600' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}>
                {isMuted ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
              </button>

              <button onClick={toggleVideo} className={`p-4 rounded-full transition-all duration-200 ${!isVideoOn ? 'bg-red-500 text-white hover:bg-red-600' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}>
                {isVideoOn ? <Video className="w-6 h-6" /> : <VideoOff className="w-6 h-6" />}
              </button>
            </div>

            {/* Center - Call Controls */}
            <div className="flex items-center space-x-4">
              <button onClick={stopRealtimeSession} className="p-4 bg-red-500 text-white rounded-full hover:bg-red-600 transition-colors">
                <PhoneOff className="w-6 h-6" />
              </button>

              <button onClick={startRealtimeSession} disabled={isRealtimeActive} className={`p-4 rounded-full transition-colors ${isRealtimeActive ? 'bg-green-600 text-white cursor-not-allowed' : 'bg-green-500 text-white hover:bg-green-600'}`}>
                <Phone className="w-6 h-6" />
              </button>
            </div>

            {/* Right Controls */}
            <div className="flex items-center space-x-4">
              <button className="p-4 bg-gray-100 text-gray-700 rounded-full hover:bg-gray-200 transition-colors">
                <Settings className="w-6 h-6" />
              </button>

              <button className="p-4 bg-gray-100 text-gray-700 rounded-full hover:bg-gray-200 transition-colors">
                <MoreVertical className="w-6 h-6" />
              </button>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="mt-6 pt-6 border-t border-gray-200">
            <h3 className="text-sm font-medium text-gray-700 mb-3">त्वरित कार्य</h3>
            <div className="flex flex-wrap gap-3">
              {quickActions.map((action, index) => (
                <button
                  key={index}
                  onClick={() => handleQuickAction(action.action)}
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors text-sm"
                >
                  {action.text}
                </button>
              ))}
              <button
                onClick={handleComprehensiveAnalysis}
                className="px-4 py-2 bg-gradient-to-r from-indigo-500 to-purple-500 text-white rounded-lg hover:from-indigo-600 hover:to-purple-600 transition-colors text-sm font-semibold"
              >
                🎯 व्यापक विश्लेषण
              </button>
            </div>
          </div>

          {/* Text Input */}
          <div className="mt-4 pt-4 border-t border-gray-200">
            <div className="flex space-x-3">
              <input
                type="text"
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="बोलकर पूछें या टाइप करें - फसल, ऋण, मंडी भाव, या सरकारी योजनाएं..."
                className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent"
              />
              <button
                onClick={handleSendMessage}
                disabled={!inputText.trim() || isLoading}
                className="px-6 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {isLoading ? 'भेज रहा है...' : 'भेजें'}
              </button>
            </div>
