import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Mic, 
  MicOff, 
  Phone, 
  PhoneOff, 
  Video, 
  VideoOff, 
  Settings, 
  MessageSquare,
  User,
  Bot,
  Volume2,
  VolumeX,
  Camera,
  CameraOff,
  MoreVertical,
  Minimize2,
  Maximize2
} from 'lucide-react';
import { useAuth } from '../hooks/useAuth';
import { useChat } from '../contexts/ChatContext';
import { toast } from 'react-hot-toast';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  metadata?: {
    language?: string;
    confidence?: number;
    voice_ready?: string[];
    intent?: string;
    suggestions?: string[];
  };
}

const Chat: React.FC = () => {
  const { user } = useAuth();
  const { messages, sendMessage, isConnected } = useChat();
  const [inputText, setInputText] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('hi');
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState<MediaRecorder | null>(null);
  const [audioChunks, setAudioChunks] = useState<Blob[]>([]);
  const [isListening, setIsListening] = useState(false);
  const [voicePrompt, setVoicePrompt] = useState('‡§Æ‡§æ‡§á‡§ï ‡§™‡§∞ ‡§ï‡•ç‡§≤‡§ø‡§ï ‡§ï‡§∞‡§ï‡•á ‡§¨‡•ã‡§≤‡•á‡§Ç');

  // Video call states
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [isKrishiMitraSpeaking, setIsKrishiMitraSpeaking] = useState(false);
  const [currentSpeaker, setCurrentSpeaker] = useState<'farmer' | 'krishimitra' | null>(null);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const krishiMitraVideoRef = useRef<HTMLVideoElement>(null);

  const languages = [
    { code: 'hi', name: '‡§π‡§ø‡§Ç‡§¶‡•Ä', flag: 'üáÆüá≥' },
    { code: 'en', name: 'English', flag: 'üáÆüá≥' },
    { code: 'bn', name: '‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ', flag: 'üáÆüá≥' },
    { code: 'ta', name: '‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç', flag: 'üáÆüá≥' },
    { code: 'te', name: '‡∞§‡±Ü‡∞≤‡±Å‡∞ó‡±Å', flag: 'üáÆüá≥' },
    { code: 'mr', name: '‡§Æ‡§∞‡§æ‡§†‡•Ä', flag: 'üáÆüá≥' },
    { code: 'gu', name: '‡™ó‡´Å‡™ú‡™∞‡™æ‡™§‡´Ä', flag: 'üáÆüá≥' },
    { code: 'pa', name: '‡®™‡©∞‡®ú‡®æ‡®¨‡©Ä', flag: 'üáÆüá≥' },
    { code: 'or', name: '‡¨ì‡¨°‡¨º‡¨ø‡¨Ü', flag: 'üáÆüá≥' },
    { code: 'ml', name: '‡¥Æ‡¥≤‡¥Ø‡¥æ‡¥≥‡¥Ç', flag: 'üáÆüá≥' },
    { code: 'kn', name: '‡≤ï‡≤®‡≥ç‡≤®‡≤°', flag: 'üáÆüá≥' }
  ];

  useEffect(() => {
    // Add welcome message
    if (messages.length === 0) {
      addMessage({
        id: 'welcome',
        text: `‡§®‡§Æ‡§∏‡•ç‡§§‡•á ${user?.name || '‡§ï‡§ø‡§∏‡§æ‡§®'}! ‡§Æ‡•à‡§Ç KrishiMitra ‡§Ü‡§™‡§ï‡•Ä ‡§ï‡•à‡§∏‡•á ‡§Æ‡§¶‡§¶ ‡§ï‡§∞ ‡§∏‡§ï‡§§‡§æ ‡§π‡•Ç‡§Ç? ‡§¨‡•ã‡§≤‡§ï‡§∞ ‡§Ö‡§™‡§®‡§æ ‡§∏‡§µ‡§æ‡§≤ ‡§™‡•Ç‡§õ‡•á‡§Ç - ‡§´‡§∏‡§≤, ‡§ã‡§£, ‡§Æ‡§Ç‡§°‡•Ä ‡§≠‡§æ‡§µ, ‡§Æ‡•å‡§∏‡§Æ ‡§Ø‡§æ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§ì‡§Ç ‡§ï‡•á ‡§¨‡§æ‡§∞‡•á ‡§Æ‡•á‡§Ç‡•§`,
        sender: 'ai',
        timestamp: new Date(),
        metadata: {
          voice_ready: [`‡§®‡§Æ‡§∏‡•ç‡§§‡•á ${user?.name || '‡§ï‡§ø‡§∏‡§æ‡§®'}! ‡§Æ‡•à‡§Ç KrishiMitra ‡§Ü‡§™‡§ï‡•Ä ‡§ï‡•à‡§∏‡•á ‡§Æ‡§¶‡§¶ ‡§ï‡§∞ ‡§∏‡§ï‡§§‡§æ ‡§π‡•Ç‡§Ç?`, "‡§¨‡•ã‡§≤‡§ï‡§∞ ‡§Ö‡§™‡§®‡§æ ‡§∏‡§µ‡§æ‡§≤ ‡§™‡•Ç‡§õ‡•á‡§Ç - ‡§´‡§∏‡§≤, ‡§ã‡§£, ‡§Æ‡§Ç‡§°‡•Ä ‡§≠‡§æ‡§µ, ‡§Æ‡•å‡§∏‡§Æ ‡§Ø‡§æ ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ø‡•ã‡§ú‡§®‡§æ‡§ì‡§Ç ‡§ï‡•á ‡§¨‡§æ‡§∞‡•á ‡§Æ‡•á‡§Ç‡•§"],
          intent: 'welcome',
          suggestions: ['‡§´‡§∏‡§≤ ‡§ï‡•Ä ‡§∏‡§≤‡§æ‡§π ‡§≤‡•á‡§Ç', '‡§ã‡§£ ‡§ï‡•Ä ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä ‡§≤‡•á‡§Ç', '‡§Æ‡§Ç‡§°‡•Ä ‡§≠‡§æ‡§µ ‡§ú‡§æ‡§®‡•á‡§Ç', '‡§Æ‡•å‡§∏‡§Æ ‡§ï‡•Ä ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä ‡§≤‡•á‡§Ç']
        }
      });
    }
  }, [messages.length, user?.name]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const addMessage = (message: Message) => {
    // This would be handled by the chat context
    console.log('Adding message:', message);
  };

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    setCurrentSpeaker('farmer');
    setIsKrishiMitraSpeaking(false);

    await sendMessage(inputText, selectedLanguage);
    setInputText('');

    // Simulate KrishiMitra response
    setTimeout(() => {
      setCurrentSpeaker('krishimitra');
      setIsKrishiMitraSpeaking(true);

      // Stop speaking after 3 seconds
      setTimeout(() => {
        setIsKrishiMitraSpeaking(false);
        setCurrentSpeaker(null);
      }, 3000);
    }, 1000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const startVoiceRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks: Blob[] = [];

      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      recorder.onstop = async () => {
        const audioBlob = new Blob(chunks, { type: 'audio/wav' });
        await processVoiceInput(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };

      setMediaRecorder(recorder);
      setAudioChunks(chunks);
      recorder.start();
      setIsRecording(true);
      setIsListening(true);
      setVoicePrompt('‡§¨‡•ã‡§≤ ‡§∞‡§π‡•á ‡§π‡•à‡§Ç...');
      setCurrentSpeaker('farmer');
      toast.success('üé§ KrishiMitra ‡§∏‡•Å‡§® ‡§∞‡§π‡§æ ‡§π‡•à‡•§ ‡§¨‡•ã‡§≤‡•á‡§Ç...');

      // Auto-stop after 15 seconds
      setTimeout(() => {
        if (recorder.state === 'recording') {
          stopVoiceRecording();
        }
      }, 15000);

    } catch (error) {
      console.error('Error starting voice recording:', error);
      toast.error('‡§Æ‡§æ‡§á‡§ï‡•ç‡§∞‡•ã‡§´‡•ã‡§® ‡§ï‡•Ä ‡§Ö‡§®‡•Å‡§Æ‡§§‡§ø ‡§¶‡•á‡§Ç');
    }
  };

  const stopVoiceRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
      setIsRecording(false);
      setIsListening(false);
      setVoicePrompt('‡§Æ‡§æ‡§á‡§ï ‡§™‡§∞ ‡§ï‡•ç‡§≤‡§ø‡§ï ‡§ï‡§∞‡§ï‡•á ‡§¨‡•ã‡§≤‡•á‡§Ç');
      setCurrentSpeaker(null);
      toast.success('‚úÖ ‡§Ü‡§µ‡§æ‡§ú ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§°‡§ø‡§Ç‡§ó ‡§¨‡§Ç‡§¶');
    }
  };

  const processVoiceInput = async (audioBlob: Blob) => {
    try {
      toast.success('üéµ OpenAI Voice API ‡§∏‡•á ‡§Ü‡§µ‡§æ‡§ú ‡§™‡•ç‡§∞‡•ã‡§∏‡•á‡§∏ ‡§π‡•ã ‡§∞‡§π‡•Ä ‡§π‡•à...');

      // Create FormData for file upload
      const formData = new FormData();
      formData.append('audio_file', audioBlob, 'voice_input.wav');
      formData.append('language', selectedLanguage);

      // Send to OpenAI Voice API endpoint
      const response = await fetch('/api/v1/voice/speech-to-text', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Voice processing failed');
      }

      const result = await response.json();

      if (result.text) {
        // Set the transcribed text and send the message
        setInputText(result.text);
        await sendMessage(result.text, result.language || selectedLanguage);

        toast.success('üéØ OpenAI Whisper ‡§∏‡•á ‡§Ü‡§µ‡§æ‡§ú ‡§∏‡§´‡§≤‡§§‡§æ‡§™‡•Ç‡§∞‡•ç‡§µ‡§ï ‡§™‡•ç‡§∞‡•ã‡§∏‡•á‡§∏ ‡§π‡•Å‡§à!');
      } else {
        throw new Error('No text detected');
      }

    } catch (error) {
      console.error('Error processing voice input:', error);

      // Fallback to mock processing for demo
      toast.success('üîÑ OpenAI API ‡§ï‡•á ‡§∏‡§æ‡§• ‡§∏‡§Æ‡§∏‡•ç‡§Ø‡§æ, ‡§°‡•á‡§Æ‡•ã ‡§Æ‡•ã‡§° ‡§Æ‡•á‡§Ç ‡§ú‡§æ ‡§∞‡§π‡•á ‡§π‡•à‡§Ç...');

      const mockVoiceTexts = {
        'hi': '‡§Æ‡•á‡§∞‡•Ä ‡§´‡§∏‡§≤ ‡§ï‡•á ‡§≤‡§ø‡§è ‡§ï‡•å‡§® ‡§∏‡•Ä ‡§´‡§∏‡§≤ ‡§∏‡§¨‡§∏‡•á ‡§Ö‡§ö‡•ç‡§õ‡•Ä ‡§π‡•ã‡§ó‡•Ä?',
        'bn': '‡¶Ü‡¶Æ‡¶æ‡¶∞ ‡¶´‡¶∏‡¶≤‡ßá‡¶∞ ‡¶ú‡¶®‡ßç‡¶Ø ‡¶ï‡ßã‡¶® ‡¶´‡¶∏‡¶≤ ‡¶∏‡¶¨‡¶ö‡ßá‡¶Ø‡¶º‡ßá ‡¶≠‡¶æ‡¶≤‡ßã ‡¶π‡¶¨‡ßá?',
        'ta': '‡Æé‡Æ©‡Øç ‡Æ™‡ÆØ‡Æø‡Æ∞‡ØÅ‡Æï‡Øç‡Æï‡ØÅ ‡Æé‡Æ®‡Øç‡Æ§ ‡Æ™‡ÆØ‡Æø‡Æ∞‡Øç ‡Æö‡Æø‡Æ±‡Æ®‡Øç‡Æ§‡Æ§‡ØÅ?',
        'te': '‡∞®‡∞æ ‡∞™‡∞Ç‡∞ü‡∞ï‡±Å ‡∞è ‡∞™‡∞Ç‡∞ü ‡∞â‡∞§‡±ç‡∞§‡∞Æ‡∞Ç?',
        'mr': '‡∞Æ‡∞æ‡∞ú‡±Ä ‡∞™‡∞ø‡∞ï‡∞æ‡∞∏‡∞æ‡∞ü‡∞ø ‡∞ï‡±ã‡∞®‡∞§‡±á ‡∞™‡±Ä‡∞ï‡±ç ‡∞∏‡∞∞‡±ç‡∞µ‡±ã‡∞§‡±ç‡∞§‡∞Æ‡±ç ‡∞Ö‡∞π‡±á?',
        'gu': '‡™Æ‡™æ‡™∞‡™æ ‡™™‡™æ‡™ï ‡™Æ‡™æ‡™ü‡´á ‡™ï‡™Ø‡´Å‡™Ç ‡™™‡™æ‡™ï ‡™∂‡´ç‡™∞‡´á‡™∑‡´ç‡™† ‡™õ‡´á?',
        'pa': '‡®Æ‡©á‡®∞‡©Ä ‡®´‡®∏‡®≤ ‡®≤‡®à ‡®ï‡®ø‡®π‡©ú‡©Ä ‡®´‡®∏‡®≤ ‡®∏‡®≠ ‡®§‡©ã‡®Ç ‡®µ‡®ß‡©Ä‡®Ü ‡®π‡©à?',
        'or': '‡¨Æ‡≠ã ‡¨´‡¨∏‡¨≤ ‡¨™‡¨æ‡¨á‡¨Å ‡¨ï‡≠á‡¨â‡¨Å ‡¨´‡¨∏‡¨≤ ‡¨∏‡¨∞‡≠ç‡¨¨‡≠ã‡¨§‡≠ç‡¨§‡¨Æ?',
        'ml': '‡¥é‡¥®‡µç‡¥±‡µÜ ‡¥µ‡¥ø‡¥≥‡¥Ø‡µç‡¥ï‡µç‡¥ï‡µç ‡¥è‡¥§‡µç ‡¥µ‡¥ø‡¥≥ ‡¥Æ‡¥ø‡¥ï‡¥ö‡µç‡¥ö‡¥§‡¥æ‡¥£‡µç?',
        'kn': '‡≤®‡≤®‡≥ç‡≤® ‡≤¨‡≥Ü‡≤≥‡≥Ü‡≤ó‡≤æ‡≤ó‡≤ø ‡≤Ø‡≤æ‡≤µ ‡≤¨‡≥Ü‡≤≥‡≥Ü ‡≤â‡≤§‡≥ç‡≤§‡≤Æ?'
      };

      const mockText = mockVoiceTexts[selectedLanguage as keyof typeof mockVoiceTexts] || mockVoiceTexts['hi'];
      setInputText(mockText);
      await sendMessage(mockText, selectedLanguage);

      toast.success('üéØ ‡§°‡•á‡§Æ‡•ã ‡§Æ‡•ã‡§° ‡§Æ‡•á‡§Ç ‡§Ü‡§µ‡§æ‡§ú ‡§™‡•ç‡§∞‡•ã‡§∏‡•á‡§∏ ‡§π‡•Å‡§à!');
    }
  };

  const handleVoiceToggle = () => {
    if (isRecording) {
      stopVoiceRecording();
    } else {
      startVoiceRecording();
    }
  };

  const handleQuickAction = async (action: string) => {
    const message = `Tell me about ${action.toLowerCase()}`;
    setInputText(message);
    await sendMessage(message, selectedLanguage);
  };

  const toggleVideo = () => {
    setIsVideoOn(!isVideoOn);
    toast.success(isVideoOn ? 'üìπ ‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§¨‡§Ç‡§¶' : 'üìπ ‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§ö‡§æ‡§≤‡•Ç');
  };

  const toggleAudio = () => {
    setIsAudioOn(!isAudioOn);
    setIsMuted(!isMuted);
    toast.success(isMuted ? 'üîä ‡§ë‡§°‡§ø‡§Ø‡•ã ‡§ö‡§æ‡§≤‡•Ç' : 'üîá ‡§ë‡§°‡§ø‡§Ø‡•ã ‡§¨‡§Ç‡§¶');
  };

  const toggleFullscreen = () => {
    setIsFullscreen(!isFullscreen);
    toast.success(isFullscreen ? 'üì± ‡§™‡•Ç‡§∞‡•ç‡§£ ‡§∏‡•ç‡§ï‡•ç‡§∞‡•Ä‡§® ‡§¨‡§Ç‡§¶' : 'üì± ‡§™‡•Ç‡§∞‡•ç‡§£ ‡§∏‡•ç‡§ï‡•ç‡§∞‡•Ä‡§® ‡§ö‡§æ‡§≤‡•Ç');
  };

  const quickActions = [
    { icon: <User className="w-5 h-5" />, text: '‡§´‡§∏‡§≤ ‡§∏‡§≤‡§æ‡§π', color: 'bg-green-500 hover:bg-green-600' },
    { icon: <Bot className="w-5 h-5" />, text: '‡§ã‡§£ ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä', color: 'bg-blue-500 hover:bg-blue-600' },
    { icon: <Volume2 className="w-5 h-5" />, text: '‡§Æ‡§Ç‡§°‡•Ä ‡§≠‡§æ‡§µ', color: 'bg-yellow-500 hover:bg-yellow-600' },
    { icon: <Camera className="w-5 h-5" />, text: '‡§Æ‡•å‡§∏‡§Æ ‡§ú‡§æ‡§®‡§ï‡§æ‡§∞‡•Ä', color: 'bg-purple-500 hover:bg-purple-600' }
  ];

  return (
    <div className={`min-h-screen bg-gradient-to-br from-green-50 to-blue-50 ${isFullscreen ? 'fixed inset-0 z-50' : ''}`}>
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-blue-500 rounded-full flex items-center justify-center">
                <Bot className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-lg font-semibold text-gray-900">KrishiMitra Video Call</h1>
                <p className="text-sm text-gray-500">
                  {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                {languages.map((lang) => (
                  <option key={lang.code} value={lang.code}>
                    {lang.flag} {lang.name}
                  </option>
                ))}
              </select>

              <button
                onClick={toggleFullscreen}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
              >
                {isFullscreen ? <Minimize2 className="w-5 h-5" /> : <Maximize2 className="w-5 h-5" />}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Video Call Interface */}
      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 h-[calc(100vh-200px)]">

          {/* Farmer's Video Panel */}
          <div className="relative bg-gray-900 rounded-xl overflow-hidden shadow-2xl">
            <div className="absolute inset-0 bg-gradient-to-br from-green-600/20 to-blue-600/20 z-10" />

            {/* Video Placeholder */}
            <div className="absolute inset-0 flex items-center justify-center">
              {isVideoOn ? (
                <div className="text-center">
                  <div className="w-32 h-32 bg-gradient-to-r from-green-500 to-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
                    <User className="w-16 h-16 text-white" />
                  </div>
                  <p className="text-white text-lg font-medium">{user?.name || '‡§ï‡§ø‡§∏‡§æ‡§®'}</p>
                  <p className="text-gray-300 text-sm">‡§Ü‡§™‡§ï‡§æ ‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã</p>
                </div>
              ) : (
                <div className="text-center">
                  <div className="w-32 h-32 bg-gray-700 rounded-full flex items-center justify-center mx-auto mb-4">
                    <VideoOff className="w-16 h-16 text-gray-400" />
                  </div>
                  <p className="text-gray-400 text-lg">‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§¨‡§Ç‡§¶ ‡§π‡•à</p>
                </div>
              )}
            </div>

            {/* Speaking Indicator */}
            {currentSpeaker === 'farmer' && (
              <div className="absolute top-4 left-4 bg-green-500 text-white px-3 py-1 rounded-full text-sm font-medium animate-pulse">
                üé§ ‡§¨‡•ã‡§≤ ‡§∞‡§π‡•á ‡§π‡•à‡§Ç
              </div>
            )}

            {/* Status Indicators */}
            <div className="absolute top-4 right-4 flex space-x-2">
              {!isAudioOn && (
                <div className="bg-red-500 text-white p-2 rounded-full">
                  <MicOff className="w-4 h-4" />
                </div>
              )}
              {!isVideoOn && (
                <div className="bg-red-500 text-white p-2 rounded-full">
                  <VideoOff className="w-4 h-4" />
                </div>
              )}
            </div>

            {/* Name Tag */}
            <div className="absolute bottom-4 left-4 bg-black/50 text-white px-3 py-1 rounded-lg">
              <span className="text-sm font-medium">{user?.name || '‡§ï‡§ø‡§∏‡§æ‡§®'}</span>
            </div>
          </div>

          {/* KrishiMitra's Video Panel */}
          <div className="relative bg-gray-900 rounded-xl overflow-hidden shadow-2xl">
            <div className="absolute inset-0 bg-gradient-to-br from-blue-600/20 to-purple-600/20 z-10" />

            {/* Video Placeholder */}
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center">
                <div className={`w-32 h-32 rounded-full flex items-center justify-center mx-auto mb-4 transition-all duration-300 ${
                  isKrishiMitraSpeaking 
                    ? 'bg-gradient-to-r from-blue-500 to-purple-500 animate-pulse' 
                    : 'bg-gradient-to-r from-blue-600 to-purple-600'
                }`}>
                  <Bot className="w-16 h-16 text-white" />
                </div>
                <p className="text-white text-lg font-medium">KrishiMitra AI</p>
                <p className="text-gray-300 text-sm">‡§Ü‡§™‡§ï‡§æ AI ‡§∏‡§π‡§æ‡§Ø‡§ï</p>
              </div>
            </div>

            {/* Speaking Indicator */}
            {currentSpeaker === 'krishimitra' && (
              <div className="absolute top-4 left-4 bg-blue-500 text-white px-3 py-1 rounded-full text-sm font-medium animate-pulse">
                ü§ñ ‡§¨‡•ã‡§≤ ‡§∞‡§π‡§æ ‡§π‡•à
              </div>
            )}

            {/* AI Status */}
            <div className="absolute top-4 right-4 bg-green-500 text-white px-3 py-1 rounded-full text-sm">
              AI ‡§∏‡§ï‡•ç‡§∞‡§ø‡§Ø
            </div>

            {/* Name Tag */}
            <div className="absolute bottom-4 left-4 bg-black/50 text-white px-3 py-1 rounded-lg">
              <span className="text-sm font-medium">KrishiMitra AI</span>
            </div>
          </div>
        </div>

        {/* Control Panel */}
