import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Mic, 
  MicOff, 
  Phone, 
  PhoneOff, 
  Video, 
  VideoOff, 
  Settings, 
  MessageSquare,
  User,
  Bot,
  Volume2,
  VolumeX,
  Camera,
  CameraOff,
  MoreVertical,
  Minimize2,
  Maximize2
} from 'lucide-react';
import { useAuth } from '../hooks/useAuth';
import { useChat } from '../contexts/ChatContext';
import { toast } from 'react-hot-toast';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  metadata?: {
    language?: string;
    confidence?: number;
    voice_ready?: string[];
    intent?: string;
    suggestions?: string[];
  };
}

const Chat: React.FC = () => {
  const { user } = useAuth();
  const { messages, sendMessage, isConnected } = useChat();
  const [inputText, setInputText] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('hi');
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState<MediaRecorder | null>(null);
  const [audioChunks, setAudioChunks] = useState<Blob[]>([]);
  const [isListening, setIsListening] = useState(false);
  const [voicePrompt, setVoicePrompt] = useState('рдорд╛рдЗрдХ рдкрд░ рдХреНрд▓рд┐рдХ рдХрд░рдХреЗ рдмреЛрд▓реЗрдВ');

  // Video call states
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [isKrishiMitraSpeaking, setIsKrishiMitraSpeaking] = useState(false);
  const [currentSpeaker, setCurrentSpeaker] = useState<'farmer' | 'krishimitra' | null>(null);
  const [realtimeSession, setRealtimeSession] = useState<any>(null);
  const [isRealtimeActive, setIsRealtimeActive] = useState(false);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const krishiMitraVideoRef = useRef<HTMLVideoElement>(null);

  const languages = [
    { code: 'hi', name: 'рд╣рд┐рдВрджреА', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'en', name: 'English', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'bn', name: 'ржмрж╛ржВрж▓рж╛', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'ta', name: 'родрооро┐ро┤рпН', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'te', name: 'р░др▒Жр░▓р▒Бр░Чр▒Б', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'mr', name: 'рдорд░рд╛рдареА', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'gu', name: 'ркЧрлБркЬрк░рк╛ркдрлА', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'pa', name: 'рикрй░риЬри╛римрйА', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'or', name: 'рмУрмбрм╝рм┐рмЖ', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'ml', name: 'р┤ор┤▓р┤пр┤╛р┤│р┤В', flag: 'ЁЯЗоЁЯЗ│' },
    { code: 'kn', name: 'р▓Хр▓ир│Нр▓ир▓б', flag: 'ЁЯЗоЁЯЗ│' }
  ];

  useEffect(() => {
    // Add welcome message
    if (messages.length === 0) {
      addMessage({
        id: 'welcome',
        text: `рдирдорд╕реНрддреЗ ${user?.name || 'рдХрд┐рд╕рд╛рди'}! рдореИрдВ KrishiMitra рдЖрдкрдХреА рдХреИрд╕реЗ рдорджрдж рдХрд░ рд╕рдХрддрд╛ рд╣реВрдВ? рдмреЛрд▓рдХрд░ рдЕрдкрдирд╛ рд╕рд╡рд╛рд▓ рдкреВрдЫреЗрдВ - рдлрд╕рд▓, рдЛрдг, рдордВрдбреА рднрд╛рд╡, рдореМрд╕рдо рдпрд╛ рд╕рд░рдХрд╛рд░реА рдпреЛрдЬрдирд╛рдУрдВ рдХреЗ рдмрд╛рд░реЗ рдореЗрдВред`,
        sender: 'ai',
        timestamp: new Date(),
        metadata: {
