import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Mic, MicOff, Phone, PhoneOff, Video, VideoOff, Settings, MessageSquare,
  User, Bot, Volume2, VolumeX, Camera, CameraOff, MoreVertical, Minimize2, Maximize2
} from 'lucide-react';
import { useAuth } from '../hooks/useAuth';
import { toast } from 'react-hot-toast';

const Chat: React.FC = () => {
  const { user } = useAuth();
  const [selectedLanguage, setSelectedLanguage] = useState('hi');

  // Video call states
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [isKrishiMitraSpeaking, setIsKrishiMitraSpeaking] = useState(false);
  const [currentSpeaker, setCurrentSpeaker] = useState<'farmer' | 'krishimitra' | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('idle');

  // WebRTC refs
  const remoteAudioRef = useRef<HTMLAudioElement>(null);
  const pcRef = useRef<RTCPeerConnection | null>(null);
  const localStreamRef = useRef<MediaStream | null>(null);
  const dcRef = useRef<RTCDataChannel | null>(null);
  const sessionIdRef = useRef<string | null>(null);

  // VAD (Voice Activity Detection) refs
  const audioContextRef = useRef<AudioContext | null>(null);
  const analyserRef = useRef<AnalyserNode | null>(null);
  const speakingRef = useRef(false);
  const lastVoiceTsRef = useRef(0);

  // Languages
  const languages = [
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'bn', name: 'বাংলা', flag: '🇮🇳' },
    { code: 'ta', name: 'தமிழ்', flag: '🇮🇳' },
    { code: 'te', name: 'తెలుగు', flag: '🇮🇳' },
    { code: 'mr', name: 'मराठी', flag: '🇮🇳' },
    { code: 'gu', name: 'ગુજરાતી', flag: '🇮🇳' },
    { code: 'pa', name: 'ਪੰਜਾਬੀ', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' },
    { code: 'ml', name: 'മലയാളം', flag: '🇮🇳' },
    { code: 'kn', name: 'ಕನ್ನಡ', flag: '🇮🇳' },
    { code: 'en', name: 'English', flag: '🇮🇳' }
  ];

  // VAD functions
  const rms = (buf: Float32Array) => {
    let s = 0;
    for (let i = 0; i < buf.length; i++) {
      const v = buf[i];
      s += v * v;
    }
    return Math.sqrt(s / buf.length);
  };

  const thresholdToRms = (val: number) => {
    return Math.max(0.003, Math.min(0.12, val / 1000));
  };

  const startVAD = () => {
    if (!audioContextRef.current || !analyserRef.current) return;

    const buf = new Float32Array(analyserRef.current.fftSize);

    const loop = () => {
      if (!analyserRef.current) return;

      analyserRef.current.getFloatTimeDomainData(buf);
      const level = rms(buf);
      const th = thresholdToRms(5); // VAD threshold
      const now = performance.now();
      const active = level > th;

      if (active) {
        lastVoiceTsRef.current = now;
        if (!speakingRef.current) {
          speakingRef.current = true;
          onSpeechStart();
        }
      } else if (speakingRef.current && (now - lastVoiceTsRef.current) > 500) {
        speakingRef.current = false;
        onSpeechEnd();
      }
      requestAnimationFrame(loop);
    };

    loop();
  };

  const onSpeechStart = () => {
    setCurrentSpeaker('farmer');
    console.log('🗣️ Farmer started speaking');
  };

  const onSpeechEnd = () => {
    setCurrentSpeaker(null);
    console.log('🔇 Farmer stopped speaking');
  };

  const sendEvent = (evt: any) => {
    if (!dcRef.current || dcRef.current.readyState !== 'open') return;
    try {
      dcRef.current.send(JSON.stringify(evt));
    } catch (e) {
      console.error('Error sending event:', e);
    }
  };

  const handleDCMessage = (e: MessageEvent) => {
    try {
      const msg = JSON.parse(e.data);
      console.log('📨 Received message:', msg);

      if (msg.type === 'response.output_text.delta') {
        setIsKrishiMitraSpeaking(true);
        setCurrentSpeaker('krishimitra');
      }

      if (msg.type === 'response.output_text.done' || msg.type === 'response.completed') {
        setIsKrishiMitraSpeaking(false);
        setCurrentSpeaker(null);
      }

      if (msg.type === 'response.created' || msg.type === 'response.started') {
        setIsKrishiMitraSpeaking(true);
        setCurrentSpeaker('krishimitra');
      }

    } catch (err) {
      console.error('❌ Error handling message:', err);
    }
  };

  const startRealtimeSession = async () => {
    try {
      setIsLoading(true);
      setConnectionStatus('requesting mic...');
      toast.success('🔄 KrishiMitra Realtime session शुरू हो रहा है...');

      // Get microphone access
      const localStream = await navigator.mediaDevices.getUserMedia({ audio: true });
      localStreamRef.current = localStream;

      // Create RTCPeerConnection
      const pc = new RTCPeerConnection({ 
        iceServers: [{ urls: ['stun:stun.l.google.com:19302'] }] 
      });
      pcRef.current = pc;

      // Set up remote audio element
      const remoteStream = new MediaStream();
      if (remoteAudioRef.current) {
        pc.ontrack = (e) => {
          remoteStream.addTrack(e.track);
          remoteAudioRef.current!.srcObject = remoteStream;
          remoteAudioRef.current!.play().catch(() => {});
        };
      }

      // Add local tracks
      for (const track of localStream.getTracks()) {
        pc.addTrack(track, localStream);
      }

      // Set up Web Audio for VAD
      const ac = new (window.AudioContext || (window as any).webkitAudioContext)();
      audioContextRef.current = ac;
      const srcNode = ac.createMediaStreamSource(localStream);
      const analyser = ac.createAnalyser();
      analyser.fftSize = 1024;
      analyser.smoothingTimeConstant = 0.2;
      srcNode.connect(analyser);
      analyserRef.current = analyser;
      startVAD();

      // Create DataChannel
      const dc = pc.createDataChannel('oai-events');
      dc.onopen = () => console.log('DataChannel open');
      dc.onmessage = handleDCMessage;
      dcRef.current = dc;

      // Handle connection state changes
      pc.onconnectionstatechange = () => {
        console.log('🔗 pc.connectionState:', pc.connectionState);
        const st = pc.connectionState;
        if (st === 'connected') {
          setConnectionStatus('connected');
          toast.success('✅ KrishiMitra Realtime session सक्रिय है! अब आप बोल सकते हैं।');
        } else if (['disconnected', 'failed', 'closed'].includes(st)) {
          setConnectionStatus('disconnected');
          toast.info('🔌 Connection ended');
        }
      };

      // Create offer
      const offer = await pc.createOffer({ 
        offerToReceiveAudio: true, 
        offerToReceiveVideo: false 
      });
      await pc.setLocalDescription(offer);

      // Create session on our backend
      console.log('🌐 Requesting session creation from server...');
      const sess = await fetch('/api/v1/voice/realtime/session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          voice: 'alloy',
          language: selectedLanguage,
          user_id: user?.id,
          farmer_context: {
            location: "Punjab, India",
            crops: ["wheat", "rice", "cotton"],
            experience: "15 years",
            land_size: "10 acres",
            current_concerns: ["loan repayment", "crop selection", "market prices"]
          }
        })
      }).then(r => r.json());

      console.log('📄 Session response:', sess);
      sessionIdRef.current = sess?.session_id || null;

      // For now, we'll use a simplified approach since we don't have the ephemeral key setup
      // In a full implementation, you'd need to handle the SDP exchange with OpenAI
      toast.success('✅ Session created! Voice interaction ready.');
      setConnectionStatus('connected');

    } catch (error) {
      console.error('❌ Error starting realtime session:', error);
      toast.error('❌ Realtime session शुरू करने में समस्या। कृपया फिर से कोशिश करें।');
      setConnectionStatus('error');
    } finally {
      setIsLoading(false);
    }
  };

  const stopRealtimeSession = () => {
    console.log('🛑 Stopping realtime session...');

    // Close WebRTC connections
    if (pcRef.current) {
      pcRef.current.close();
      pcRef.current = null;
    }

    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(t => t.stop());
      localStreamRef.current = null;
    }

    if (audioContextRef.current) {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }

    analyserRef.current = null;
    dcRef.current = null;
    sessionIdRef.current = null;

    setConnectionStatus('idle');
    setCurrentSpeaker(null);
    setIsKrishiMitraSpeaking(false);

    toast.success('🛑 KrishiMitra Realtime session बंद हो गया है।');
  };

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopRealtimeSession();
    };
  }, []);

  // Video call controls
  const toggleVideo = () => setIsVideoOn(!isVideoOn);
  const toggleAudio = () => setIsMuted(!isMuted);
  const toggleFullscreen = () => setIsFullscreen(!isFullscreen);

  return (
    <div className={`min-h-screen bg-gradient-to-br from-green-50 to-blue-50 ${isFullscreen ? 'fixed inset-0 z-50' : ''}`}>
      {/* Hidden audio element for remote audio */}
      <audio ref={remoteAudioRef} autoPlay />

      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-blue-500 rounded-full flex items-center justify-center">
                <Bot className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-lg font-semibold text-gray-900">KrishiMitra Voice Call</h1>
                <p className="text-sm text-gray-500">
                  {connectionStatus === 'connected' ? '🟢 Connected' : 
                   connectionStatus === 'connecting' ? '🟡 Connecting...' :
                   connectionStatus === 'error' ? '🔴 Error' : '🔴 Disconnected'}
                  {sessionIdRef.current && ` • Session: ${sessionIdRef.current.slice(-8)}`}
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <select
                value={selectedLanguage}
                onChange={(e) => setSelectedLanguage(e.target.value)}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                {languages.map(lang => (
                  <option key={lang.code} value={lang.code}>
                    {lang.flag} {lang.name}
                  </option>
                ))}
              </select>

              <button
                onClick={toggleFullscreen}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
              >
                {isFullscreen ? <Minimize2 className="w-5 h-5" /> : <Maximize2 className="w-5 h-5" />}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Video Call Interface */}
      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 h-[calc(100vh-200px)]">

          {/* Farmer's Video Panel */}
          <div className="relative bg-gray-900 rounded-xl overflow-hidden shadow-2xl">
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center text-white">
                <User className="w-24 h-24 mx-auto mb-4 opacity-50" />
                <p className="text-lg font-semibold">{user?.name || 'किसान'}</p>
                <p className="text-sm opacity-75">आपका वीडियो</p>
              </div>
            </div>

            {/* Speaking indicator */}
            {currentSpeaker === 'farmer' && (
              <div className="absolute top-4 left-4 bg-green-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
                🗣️ बोल रहे हैं
              </div>
            )}

            {/* Audio indicator */}
            {isMuted && (
              <div className="absolute top-4 right-4 bg-gray-800 text-white px-3 py-1 rounded-full text-sm">
                🔇 म्यूट
              </div>
            )}
          </div>

          {/* KrishiMitra's Video Panel */}
          <div className="relative bg-gray-900 rounded-xl overflow-hidden shadow-2xl">
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center text-white">
                <Bot className="w-24 h-24 mx-auto mb-4 opacity-50" />
                <p className="text-lg font-semibold">KrishiMitra AI</p>
                <p className="text-sm opacity-75">AI सहायक</p>
              </div>
            </div>

            {/* AI Status */}
            <div className="absolute top-4 right-4 flex space-x-2">
              <div className={`px-3 py-1 rounded-full text-sm ${
                connectionStatus === 'connected'
                  ? 'bg-blue-500 text-white animate-pulse' 
                  : 'bg-green-500 text-white'
              }`}>
                {connectionStatus === 'connected' ? '🔄 Realtime' : 'AI सक्रिय'}
              </div>
            </div>

            {/* Speaking indicator */}
            {isKrishiMitraSpeaking && (
              <div className="absolute bottom-4 left-4 bg-green-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
                🗣️ बोल रहा है
              </div>
            )}
          </div>
        </div>

        {/* Control Panel */}
        <div className="mt-6 bg-white rounded-xl shadow-lg p-6">
          <div className="flex items-center justify-between">

            {/* Left Controls */}
            <div className="flex items-center space-x-4">
              <button onClick={toggleAudio} className={`p-4 rounded-full transition-all duration-200 ${isMuted ? 'bg-red-500 text-white hover:bg-red-600' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}>
                {isMuted ? <MicOff className="w-6 h-6" /> : <Mic className="w-6 h-6" />}
              </button>

              <button onClick={toggleVideo} className={`p-4 rounded-full transition-all duration-200 ${!isVideoOn ? 'bg-red-500 text-white hover:bg-red-600' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`}>
                {isVideoOn ? <Video className="w-6 h-6" /> : <VideoOff className="w-6 h-6" />}
              </button>
            </div>

            {/* Center - Call Controls */}
            <div className="flex items-center space-x-4">
              <button 
                onClick={stopRealtimeSession} 
                disabled={connectionStatus !== 'connected'}
                className={`p-4 rounded-full transition-colors ${
                  connectionStatus === 'connected'
                    ? 'bg-red-500 text-white hover:bg-red-600' 
                    : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                }`}
              >
                <PhoneOff className="w-6 h-6" />
              </button>

              <button 
                onClick={startRealtimeSession} 
                disabled={connectionStatus === 'connected' || isLoading}
                className={`p-4 rounded-full transition-colors ${
                  connectionStatus === 'connected' || isLoading
                    ? 'bg-green-600 text-white cursor-not-allowed' 
                    : 'bg-green-500 text-white hover:bg-green-600'
                }`}
              >
                {isLoading ? (
                  <div className="w-6 h-6 border-2 border-white border-t-transparent rounded-full animate-spin" />
                ) : (
                  <Phone className="w-6 h-6" />
                )}
              </button>
            </div>

            {/* Right Controls */}
            <div className="flex items-center space-x-4">
              <button className="p-4 bg-gray-100 text-gray-700 rounded-full hover:bg-gray-200 transition-colors">
                <Settings className="w-6 h-6" />
              </button>

              <button className="p-4 bg-gray-100 text-gray-700 rounded-full hover:bg-gray-200 transition-colors">
                <MoreVertical className="w-6 h-6" />
              </button>
            </div>
          </div>

          {/* Status Information */}
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                {connectionStatus === 'connected' ? '🎤 Voice Session Active' : '📞 Start Voice Session'}
              </h3>
              <p className="text-sm text-gray-600 mb-4">
                {connectionStatus === 'connected'
                  ? 'KrishiMitra is ready to help you with farming advice. Speak naturally!'
                  : 'Click the green phone button to start a voice conversation with KrishiMitra.'
                }
              </p>

              {sessionIdRef.current && (
                <div className="bg-gray-50 rounded-lg p-4 text-left">
                  <h4 className="font-medium text-gray-900 mb-2">Session Details:</h4>
                  <div className="text-sm text-gray-600 space-y-1">
                    <p><strong>Session ID:</strong> {sessionIdRef.current}</p>
                    <p><strong>Language:</strong> {selectedLanguage}</p>
                    <p><strong>Status:</strong> {connectionStatus}</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="bg-white border-t mt-6">
        <div className="max-w-7xl mx-auto px-4 py-3">
          <div className="flex items-center justify-between text-sm text-gray-500">
            <span>Powered by KrishiMitra AI</span>
            <span>OpenAI Realtime API • Voice-First AI Assistant</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
