import React, { useState, useEffect, useRef, FC } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Send, 
  Mic, 
  MicOff, 
  Volume2, 
  Settings, 
  User, 
  Bot, 
  Loader2, 
  Calendar, 
  MapPin, 
  TrendingUp,
  Sparkles,
  Brain,
  Leaf,
  DollarSign,
  Shield
} from 'lucide-react';
import { useChat } from '../contexts/ChatContext';
import toast from 'react-hot-toast';

const Chat: React.FC = () => {
  const [inputText, setInputText] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('hi');
  const [showVoiceSettings, setShowVoiceSettings] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState<MediaRecorder | null>(null);
  const [audioChunks, setAudioChunks] = useState<Blob[]>([]);
  const [isListening, setIsListening] = useState(false);
  const [voicePrompt, setVoicePrompt] = useState('माइक पर क्लिक करके बोलें');

  const { messages, sendMessage, isLoading } = useChat();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const languages = [
    { code: 'hi', name: 'हिंदी', flag: '🇮🇳' },
    { code: 'bn', name: 'বাংলা', flag: '🇮🇳' },
    { code: 'ta', name: 'தமிழ்', flag: '🇮🇳' },
    { code: 'te', name: 'తెలుగు', flag: '🇮🇳' },
    { code: 'mr', name: 'मराठी', flag: '🇮🇳' },
    { code: 'gu', name: 'ગુજરાતી', flag: '🇮🇳' },
    { code: 'pa', name: 'ਪੰਜਾਬੀ', flag: '🇮🇳' },
    { code: 'or', name: 'ଓଡ଼ିଆ', flag: '🇮🇳' },
    { code: 'ml', name: 'മലയാളം', flag: '🇮🇳' },
    { code: 'kn', name: 'ಕನ್ನಡ', flag: '🇮🇳' }
  ];

  const quickActions = [
    { icon: <Leaf className="w-5 h-5" />, text: 'फसल सलाह', color: 'from-green-500 to-emerald-600' },
    { icon: <DollarSign className="w-5 h-5" />, text: 'ऋण सहायता', color: 'from-blue-500 to-indigo-600' },
    { icon: <TrendingUp className="w-5 h-5" />, text: 'मंडी भाव', color: 'from-orange-500 to-red-600' },
    { icon: <Shield className="w-5 h-5" />, text: 'जोखिम चेतावनी', color: 'from-purple-500 to-pink-600' },
    { icon: <Calendar className="w-5 h-5" />, text: 'खेती कैलेंडर', color: 'from-teal-500 to-cyan-600' },
    { icon: <MapPin className="w-5 h-5" />, text: 'स्थानीय योजनाएं', color: 'from-yellow-500 to-orange-600' }
  ];

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async () => {
    if (inputText.trim()) {
      await sendMessage(inputText, selectedLanguage);
      setInputText('');
      setIsTyping(true);

      // Simulate AI typing
      setTimeout(() => {
        setIsTyping(false);
      }, 2000);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const startVoiceRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks: Blob[] = [];

      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      recorder.onstop = async () => {
        const audioBlob = new Blob(chunks, { type: 'audio/wav' });
        await processVoiceInput(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };

      setMediaRecorder(recorder);
      setAudioChunks(chunks);
      recorder.start();
      setIsRecording(true);
      setIsListening(true);
      setVoicePrompt('बोल रहे हैं...');
      toast.success('🎤 KrishiMitra सुन रहा है। बोलें...');

      // Auto-stop after 15 seconds
      setTimeout(() => {
        if (recorder.state === 'recording') {
          stopVoiceRecording();
        }
      }, 15000);

    } catch (error) {
      console.error('Error starting voice recording:', error);
      toast.error('माइक्रोफोन की अनुमति दें');
    }
  };

  const stopVoiceRecording = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
      setIsRecording(false);
      setIsListening(false);
      setVoicePrompt('माइक पर क्लिक करके बोलें');
      toast.success('✅ आवाज रिकॉर्डिंग बंद');
    }
  };

  const processVoiceInput = async (audioBlob: Blob) => {
    try {
      toast.success('🎵 OpenAI Voice API से आवाज प्रोसेस हो रही है...');

      // Create FormData for file upload
      const formData = new FormData();
      formData.append('audio_file', audioBlob, 'voice_input.wav');
      formData.append('language', selectedLanguage);

      // Send to OpenAI Voice API endpoint
      const response = await fetch('/api/v1/voice/speech-to-text', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Voice processing failed');
      }

      const result = await response.json();

      if (result.text) {
        // Set the transcribed text and send the message
        setInputText(result.text);
        await sendMessage(result.text, result.language || selectedLanguage);

        toast.success('🎯 OpenAI Whisper से आवाज सफलतापूर्वक प्रोसेस हुई!');
      } else {
        throw new Error('No text detected');
      }

    } catch (error) {
      console.error('Error processing voice input:', error);

      // Fallback to mock processing for demo
      toast.success('🔄 OpenAI API के साथ समस्या, डेमो मोड में जा रहे हैं...');

      const mockVoiceTexts = {
        'hi': 'मेरी फसल के लिए कौन सी फसल सबसे अच्छी होगी?',
        'bn': 'আমার ফসলের জন্য কোন ফসল সবচেয়ে ভালো হবে?',
        'ta': 'என் பயிருக்கு எந்த பயிர் சிறந்தது?',
        'te': 'నా పంటకు ఏ పంట ఉత్తమం?',
        'mr': 'మాజీ పికాసాటి కోనతే పీక్ సర్వోత్తమ్ అహే?',
        'gu': 'મારા પાક માટે કયું પાક શ્રેષ્ઠ છે?',
        'pa': 'ਮੇਰੀ ਫਸਲ ਲਈ ਕਿਹੜੀ ਫਸਲ ਸਭ ਤੋਂ ਵਧੀਆ ਹੈ?',
        'or': 'ମୋ ଫସଲ ପାଇଁ କେଉଁ ଫସଲ ସର୍ବୋତ୍ତମ?',
        'ml': 'എന്റെ വിളയ്ക്ക് ഏത് വിള മികച്ചതാണ്?',
        'kn': 'ನನ್ನ ಬೆಳೆಗಾಗಿ ಯಾವ ಬೆಳೆ ಉತ್ತಮ?'
      };

      const mockText = mockVoiceTexts[selectedLanguage as keyof typeof mockVoiceTexts] || mockVoiceTexts['hi'];
      setInputText(mockText);
      await sendMessage(mockText, selectedLanguage);

      toast.success('🎯 डेमो मोड में आवाज प्रोसेस हुई!');
    }
  };

  const handleVoiceToggle = () => {
    if (isRecording) {
      stopVoiceRecording();
    } else {
      startVoiceRecording();
    }
  };

  const handleQuickAction = async (action: string) => {
    const message = `Tell me about ${action.toLowerCase()}`;
    await sendMessage(message, selectedLanguage);
    toast.success(`Selected: ${action}`);
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('en-US', { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: true 
    });
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50">
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-md border-b border-green-100 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-gradient-to-br from-green-500 to-emerald-600 rounded-xl flex items-center justify-center">
              <Brain className="w-6 h-6 text-white" />
            </div>
            <div>
              <h1 className="text-xl font-bold text-gray-900">KrishiSampann AI</h1>
              <p className="text-sm text-gray-600">Your farming companion</p>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <button
              onClick={() => setShowVoiceSettings(!showVoiceSettings)}
              className="p-2 text-gray-600 hover:text-green-600 transition-colors"
            >
              <Settings className="w-5 h-5" />
            </button>
            <div className="flex items-center space-x-2">
              <div className={`w-2 h-2 rounded-full animate-pulse ${isRecording ? 'bg-red-500' : 'bg-green-500'}`}></div>
              <span className={`text-sm font-medium ${isRecording ? 'text-red-600' : 'text-green-600'}`}>
                {isRecording ? 'Recording...' : 'AI Online'}
              </span>
            </div>
          </div>
        </div>
      </header>

      {/* Voice Settings Panel */}
      <AnimatePresence>
        {showVoiceSettings && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="bg-white border-b border-green-100 px-6 py-4"
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <span className="text-sm font-medium text-gray-700">Language:</span>
                  <select
                    value={selectedLanguage}
                    onChange={(e) => setSelectedLanguage(e.target.value)}
                    className="border border-green-200 rounded-lg px-3 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-green-500"
                  >
                    {languages.map((lang) => (
                      <option key={lang.code} value={lang.code}>
                        {lang.flag} {lang.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="flex items-center space-x-2">
                  <span className="text-sm font-medium text-gray-700">Voice:</span>
                  <button className="p-2 text-gray-600 hover:text-green-600 transition-colors">
                    <Volume2 className="w-4 h-4" />
                  </button>
                </div>
              </div>

              <div className="text-sm text-gray-500">
                Current: {languages.find(l => l.code === selectedLanguage)?.flag} {languages.find(l => l.code === selectedLanguage)?.name}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Quick Actions */}
      <div className="px-6 py-4 bg-white/60 border-b border-green-100">
        <div className="flex items-center space-x-3 overflow-x-auto pb-2">
          {quickActions.map((action, index) => (
            <motion.button
              key={index}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: index * 0.1 }}
              onClick={() => handleQuickAction(action.text)}
              className={`flex items-center space-x-2 px-4 py-2 rounded-full text-white font-medium text-sm whitespace-nowrap bg-gradient-to-r ${action.color} hover:shadow-lg transition-all duration-300 transform hover:-translate-y-0.5`}
            >
              {action.icon}
              <span>{action.text}</span>
            </motion.button>
          ))}
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto px-6 py-4 space-y-4">
        {messages.length === 0 ? (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center py-12"
          >
            <div className="w-20 h-20 bg-gradient-to-br from-green-100 to-emerald-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Sparkles className="w-10 h-10 text-green-600" />
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">Welcome to KrishiSampann AI!</h3>
            <p className="text-gray-600 mb-6">
              Ask me anything about farming, loans, market prices, or government schemes. 
              I'm here to help you make better decisions.
            </p>
            <div className="flex items-center justify-center space-x-2 text-sm text-gray-500">
              <Mic className="w-4 h-4" />
              <span>Try voice input or type your question below</span>
            </div>
          </motion.div>
        ) : (
          messages.map((message, index) => (
            <motion.div
              key={message.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div className={`flex items-start space-x-3 max-w-xs lg:max-w-md ${message.sender === 'user' ? 'flex-row-reverse space-x-reverse' : ''}`}>
                <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                  message.sender === 'user' 
                    ? 'bg-gradient-to-br from-blue-500 to-indigo-600' 
                    : 'bg-gradient-to-br from-green-500 to-emerald-600'
                }`}>
                  {message.sender === 'user' ? (
                    <User className="w-4 h-4 text-white" />
                  ) : (
                    <Bot className="w-4 h-4 text-white" />
                  )}
                </div>

                <div className={`rounded-2xl px-4 py-3 ${
                  message.sender === 'user'
                    ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white'
                    : 'bg-white text-gray-800 border border-green-100 shadow-sm'
                }`}>
                  <p className="text-sm leading-relaxed">{message.text}</p>
                  <div className={`text-xs mt-2 ${
                    message.sender === 'user' ? 'text-blue-100' : 'text-gray-500'
                  }`}>
                    {formatTime(message.timestamp)}
                  </div>
                </div>
              </div>
            </motion.div>
          ))
        )}

        {/* Typing Indicator */}
        {isTyping && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="flex justify-start"
          >
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-gradient-to-br from-green-500 to-emerald-600 rounded-full flex items-center justify-center">
                <Bot className="w-4 h-4 text-white" />
              </div>
              <div className="bg-white rounded-2xl px-4 py-3 border border-green-100 shadow-sm">
                <div className="flex items-center space-x-2">
                  <Loader2 className="w-4 h-4 text-green-600 animate-spin" />
                  <span className="text-sm text-gray-600">AI is thinking...</span>
                </div>
              </div>
            </div>
          </motion.div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="bg-white border-t border-green-100 px-6 py-4">
        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <input
              ref={inputRef}
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="बोलकर पूछें या टाइप करें - फसल, ऋण, मंडी भाव, या सरकारी योजनाएं..."
              className="w-full px-4 py-3 border border-green-200 rounded-2xl focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent text-gray-700 placeholder-gray-400"
              disabled={isLoading}
            />
            {selectedLanguage !== 'en' && (
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                <span className="text-xs bg-green-100 text-green-700 px-2 py-1 rounded-full">
                  {languages.find(l => l.code === selectedLanguage)?.flag}
                </span>
              </div>
            )}
          </div>

          <button
            onClick={handleVoiceToggle}
            className={`p-3 rounded-full transition-all duration-300 ${
              isRecording 
                ? 'bg-red-500 text-white shadow-lg animate-pulse' 
                : 'bg-green-500 text-white hover:bg-green-600 hover:shadow-lg'
            }`}
            disabled={isLoading}
          >
            {isRecording ? <MicOff className="w-5 h-5" /> : <Mic className="w-5 h-5" />}
          </button>

          <button
            onClick={handleSendMessage}
            disabled={!inputText.trim() || isLoading}
            className="p-3 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-full hover:from-green-600 hover:to-emerald-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        <div className="mt-3 flex items-center justify-between text-xs text-gray-500">
          <span>Press Enter to send, Shift+Enter for new line</span>
          <span>Powered by KrishiMitra AI</span>
        </div>
      </div>

      {/* Hidden audio element for voice playback */}
      <audio id="audio-player" style={{ display: 'none' }} />
    </div>
  );
};

export default Chat;
